          <node TEXT="视觉适应性" ID="ptTNaB7hfu" _mubu_text="%3Cspan%3E%E8%A7%86%E8%A7%89%E9%80%82%E5%BA%94%E6%80%A7%3C/span%3E" STYLE="fork">
